import { useState } from "react";

const App = () => {
  return (
    <div>
      <Student name={"Djarot Purnomo"} isStudent={true} />
    </div>
  );
};

function Student({ name, isStudent }) {
  let student = "";
  if (isStudent === true){
    student = "Student";
  } else {
    student = "Not student"
  }

  let names = name.split(" ")[0];

  return (
    <div>
      <p className="fullName">{name}</p>
      <p className="firstName">{names}</p>
      <p className="status">{student}</p>
    </div>
  )
}

export default App;
export { Student };
