import { useState, useEffect } from "react";

const App = () => {
  // state untuk menyimpan data
  const [data, setData] = useState(null);
  // state untuk menyimpan status loading
  const [loading, setLoading] = useState(true);
  // state untuk menyimpan error
  const [error, setError] = useState(null);

  // useEffect akan dijalankan ketika komponen pertama kali di-mount
  useEffect(() => {
    // fetch data dari API
    fetch("https://ghibliapi.herokuapp.com/films/58611129-2dbc-4a81-a72f-77ddfc1b1b49")
    // ubah response menjadi JSON
    .then((response) => response.json())
    // simpan data ke dalam state
    .then((json) => {
      // set data
      setData(json);
      // set loading menjadi false
      setLoading(false);
    })
    // handle error
    .catch((error) => {
      // set error
      setError(error);
      // set loading menjadi false
      setLoading(false);
    });
  }, []);

  // jika sedang loading, tampilkan loading
  if (loading) return "Loading...";

  // jika terjadi error, tampilkan error
  if (error) return "Error!";

  // Jika data sudah ada, tampilkan data
  return (
  <div>
    <p>{JSON.stringify(data)}</p>
    <select>
      <option>All</option>
      <option>Basic</option>
      <option>Basic with Creator</option>
    </select>
  </div>
  );
};

export default App;
