function Table({ mentors }) {
  return (
    <table>
      <thead>
        <tr>
          <th>Name</th>
          <th>House</th>
          <th>Email</th>
        </tr>
      </thead>
      <tbody>
        {
          mentors.length <= 0 ? (
            <tr>
              <td>Data not available</td>
              <td>Data not available</td>
              <td>Data not available</td>
            </tr>
          ) : (
            mentors.map((mentor) => (
              <tr style={{
                backgroundColor: mentor.id % 2 === 0 ? "" : "lightcyan"
              }}>
                <td>{mentor.name}</td>
                <td>{mentor.house}</td>
                <td>{mentor.email}</td>
              </tr>
            ))
          )
        }
      </tbody>
    </table>
  );
}

export default Table;
